mapper.select:
SELECT lno, cate, title, content as content, img, price as price, status, sdate, edate, tdate, tname, amt FROM lecture a JOIN teacher b ON a.tcode = b.tcode
AND a.cate = #{category}
ORDER BY lno DESC

SELECT lno, cateName, title, a.content as content, img, a.price as price, status, sdate, edate, tdate, tname, bname, amt
FROM lecture a JOIN teacher b ON a.tcode = b.tcode JOIN book c ON a.bcode = c.bcode JOIN category d ON a.cate=d.cate
WHERE lno=#{lno}
