mapper.select:
SELECT lno, a.cate as cate,cateName, a.title as title, a.content as content,img, a.price as price, status, sdate, edate, tdate, tname, amt FROM lecture a JOIN teacher b ON a.tcode = b.tcode JOIN category c ON a.cate=c.cate
AND a.cate = #{category}
ORDER BY lno DESC

SELECT lno,c.bcode as bcode, cateName, title, a.content as content, img, a.price as price, status, sdate, edate, tdate, tname, bname, amt
FROM lecture a JOIN teacher b ON a.tcode = b.tcode JOIN book c ON a.bcode = c.bcode JOIN category d ON a.cate=d.cate
WHERE lno=#{lno}
